import React from 'react'
import style from '../styles/dishes.css'


const Card = (props) => {
    let badgeText
    if (props.products === 0){
        badgeText = 'Выберите другое блюдо'
    }
    else if (props.sale === true){
        badgeText = 'АКЦИЯ'
    }

    const [isShown, setIsShown] = React.useState(false)

    function toggleShown(){
        setIsShown(prevShown=>!prevShown)
    }
  return (
    <div>
        <link rel="stylesheet" href={style}></link>
        <div className="card">
            {badgeText && <div className='card--badge'>{badgeText}</div>}
            <img src={props.coverImg} className="card--image" />
            <div className="card--stats">
                <img src={props.img} className="card--star" />
                <span>{props.stats.rating} </span>
                <span className="gray">({props.stats.reviewCount}) • </span>
                {badgeText ? <span className="gray">{badgeText}</span> : <span className="gray">{props.location}</span>}
            </div>
            <p className="card--title">{props.title}</p>
            <p className="card--price"><span className="bold">From ${props.price}</span> / person</p>
            {isShown && <p>{props.description}</p>}
            <button onClick={toggleShown}>Подробнее</button>
        </div>
    </div>
  )
}

export default Card
