import React from 'react'
import style from '../styles/crud.css'
import Axios from 'axios'


const [image, setImage] = React.useState(null);

const onChange = e => {
const file = e.target.files[0];
const reader = new FileReader();
reader.readAsBinaryString(file);
reader.onloadend = () => {
  setImage(reader.result);
};

const CrudDish = () => {
    const [formData, setFormData] = React.useState({
        title: '',
        description: '',
        price: '',
        rating: '',
        sale: true,
        products: true
    })
    formData.append('image', image);

  }

    function handleChange(event) {
        const {name, value, type, checked} = event.target
        setFormData(prevFormData => {
            return{
                ...prevFormData,
                [name]: type === 'checkbox' ? checked : value
            }
        })
    }

    function handleSubmit(event){
        event.preventDefault()
        Axios.post('http://localhost:3001/api/insert', {dishTitle: formData.title,
        dishDesc: formData.description,
        dishPrice: formData.price,
        dishRating: formData.rating,
        dishSale: formData.sale,
        dishImg: image,
        dishProd: formData.products}).then(()=>{
            alert('successful insert')
        })
        console.log(formData)
    }

  return (
    <main>
        <link rel="stylesheet" href={style} />
        <div className='form' >
            <input type="file" accept='image/jpeg, image/svg+xml, image/png'
            placeholder='Фото блюда'
            name="coverImg"
            value={image}
            onChange = {onChange}
            />

            <input type='text'
            placeholder='Название блюда'
            name='title'
            value={formData.title}
            onChange = {handleChange}/>

            <input type='text'
            placeholder='Описание блюда'
            name='description'
            value={formData.description}
            onChange={handleChange}
            />

            <input type='text'
            placeholder='Стоимость'
            name='price'
            value={formData.price}
            onChange={handleChange}/>

            <input type='text'
            placeholder='Рейтинг'
            name='rating'
            value={formData.rating}
            onChange={handleChange}/>

            <input 
            type="checkbox"
            name='sale'
            id='sale'
            checked={formData.sale}
            onChange={handleChange}
             />

            <label htmlFor='sale'>На блюдо действует скидка?</label>

            <input 
            type="checkbox"
            name='products'
            id='products'
            checked={formData.products}
            onChange={handleChange}
             />

            <label htmlFor='products'>Продукты в наличии?</label>
            <br />

            <button className="form--button" onClick={handleSubmit}>Подтвердить</button>
        </div>
    </main>
  )
}

export default CrudDish
