import React from 'react'
import style from '../styles/crud.css'

const CrudDish = () => {
    const [formData, setFormData] = React.useState({
        title: '',
        description: '',
        price: 0,
        coverImg: "https://images.pexels.com/photos/10720380/pexels-photo-10720380.jpeg?auto=compress&cs=tinysrgb&w=800",
        stats: {
            rating: 5.0,
            reviewCount: 0
        },
        sale: true,
        products: true,
    })

    function handleChange(event) {
        const [name, value, type, checked] = event.target
        setFormData(prevFormData => {
            return{
                ...prevFormData,
                [name]: type === 'checkbox' ? checked : value
            }
        })
    }

    function handleSubmit(event){
        event.preventDefault()
        console.log(formData)
    }

  return (
    <main>
        <link rel="stylesheet" href={style} />
        <div className='form' >
            <input type='text'
            placeholder='Название блюда'
            name='title'
            value={formData.title}
            onChange = {handleChange}/>

            <input type='text'
            placeholder='Описание блюда'
            name='description'
            value={formData.description}
            onChange={handleChange}
            />

            <input type='number'
            placeholder='Стоимость'
            name='price'
            value={formData.price}
            onChange={handleChange}/>

            <input type='text'
            placeholder='Рейтинг'
            name='rating'
            value={formData.stats.rating}
            onChange={handleChange}/>

            <input 
            type="checkbox"
            name='sale'
            id='sale'
            checked={formData.sale}
            onChange={handleChange}
             />

            <label htmlFor='sale'>На блюдо действует скидка?</label>
            <br />

            <input 
            type="checkbox"
            name='products'
            id='products'
            checked={formData.products}
            onChange={handleChange}
             />

            <label htmlFor='products'>Продукты в наличии?</label>
            <br />

            <button>Подтвердить</button>
        </div>
    </main>
  )
}

export default CrudDish
